mapper.insert:
INSERT INTO board.comment(bno, content, writer, deleteYn, createdDate, modifiedDate)
VALUES ( #{bno}
       , #{content}
       , #{writer}
       , 0
       , NOW()
       , NULL)

mapper.update:
UPDATE
  board.comment
SET modifiedDate = NOW(),
    content      = #{content}
WHERE cno = #{cno}
