mapper.insert:
INSERT INTO board.post (bno, title, content, writer, viewCnt, noticeYn, deleteYn, createdDate,
                        modifiedDate)
VALUES ( #{bno}
       , #{title}
       , #{content}
       , #{writer}
       , 0
       , #{noticeYn}
       , 0
       , NOW()
       , NULL)

mapper.update:
update board.post
set modifiedDate = NOW()
  , title        = #{title}
  , content      = #{content}
  , writer       = #{writer}
  , noticeYn     = #{noticeYn}
WHERE bno = #{bno}

mapper.delete:
UPDATE board.post
SET deleteYn = 1
WHERE bno = #{bno}
