mapper.insert:
INSERT INTO board.user (uno, name, email, passWord, nickName, gender, phoneNumber, birth, createdDate, modifiedDate)
VALUES ( #{uno}
       , #{name}
       , #{email}
       , #{passWord}
       , #{nickName}
       , #{gender}
       , #{phoneNumber}
       , #{birth}
       , NOW()
       , NULL)

mapper.update:
update board.post
set modifiedDate = NOW()
  , title         = #{title}
  , content       = #{content}
  , writer        = #{writer}
  , noticeYn     = #{noticeYn}
WHERE bno = #{bno}

mapper.delete:
UPDATE board.post
SET deleteYn = 1
WHERE bno = #{bno}
